mapper.update:
update STORE_INFO_UPDATE set
	su_state=#{su_state},
	su_reason=#{su_reason}
	where su_idx=#{su_idx}

update STORE set
	store_name=#{su_name},
	store_addr=#{su_addr},
	store_daddr=#{su_daddr},
	store_tel=#{su_tel}
	where store_idx=#{store_idx}

update STORE_ACCOUNT set
	store_ceo=#{su_ceo}
	where store_idx=#{store_idx}
