mapper.select:
select count(*) from review, reservation
where review.reserve_idx=reservation.reserve_idx
and store_idx=#{store_idx}

select * from (select rownum rn, d.* from (select jcnt, c.* from (select b.*, store_img from (select a.* from (select * from store where store_state=upper('true')
and store_addr like '%'||#{cityWord}||'%'||#{unitWord}||'%'
)a, store_time where stime_day=#{week} and store_time.store_idx=a.store_idx)b, store_img where b.store_idx=store_img.store_idx and img_order=1)c, (select store_idx, count(*) jcnt from jjim group by store_idx)j where c.store_idx=j.store_idx order by jcnt desc)d) where rn between 1 and 4

select * from (select rownum rn, d.* from (select point, c.* from (select b.*, store_img from (select a.* from (select * from store where store_state=upper('true')
and store_addr like '%'||#{cityWord}||'%'||#{unitWord}||'%'
)a, store_time where stime_day=#{week} and store_time.store_idx=a.store_idx)b, store_img where b.store_idx=store_img.store_idx and img_order=1)c, (select store_idx, round(avg(rev_score),2) point from review, reservation where reservation.reserve_idx=review.reserve_idx group by store_idx)p where c.store_idx=p.store_idx order by point desc)d) where rn between 1 and 4

select cate_value_name from category, cate_value
where store_idx=#{store_idx}
and category.cate_value_idx = cate_value.cate_value_idx
